import { graphql, buildSchema } from 'graphql'

const schema = buildSchema(`
  type Query {
    hello: String
  }
`)

const root = {
  hello() {
    return 'world'
  }
}

graphql(schema, '{ hello }', root).then(res => {
  console.log(res)
})
// => { data: { hello: 'world' } }

import {
  graphql,
  GraphQLSchema,
  GraphQLObjectType,
  GraphQLString
} from 'graphql'

var schema = new GraphQLSchema({
  query: new GraphQLObjectType({
    name: 'RootQueryType',
    fields: {
      hello: {
        type: GraphQLString,
        resolve() {
          return 'world'
        }
      }
    }
  })
})

graphql(schema, '{ hello }').then(res => {
  console.log(res)
})
// => { data: { hello: 'world' } }
