children.value:
.mission-list {
  box-sizing: border-box;
  font-size: .86em;
}
.mission-item {
  box-sizing: border-box;
  margin: 8px;
  padding: 16px;
  border: solid 1px rgba(30, 144, 255, 0.2);
  box-shadow: 0 4px 8px rgba(30, 144, 255, 0.07);
  border-radius: 3px;
  background: white;
}
@media (min-width: 481px) {
  .mission-list {
    display: flex;
    margin-left: 32px;
    margin-right: 32px;
    justify-items: center;
  }
  .mission-item {
    flex: 0 0 25%;
    margin: 4px;
  }
}
.mission-item > h3,
.mission-item > ul,
.mission-item > ul > li {
  margin: 0;
  padding: 0;
  border: 0;
  list-style-type: none;
  background: transparent;
}
.mission-item > h3 {
  padding-bottom: 8px;
  margin-bottom: 8px;
  border-bottom: solid 1px rgba(30, 144, 255, 0.2);
}
.mission-item.mission-item > h3 > em {
  font-style: normal;
  font-weight: 400;
  font-size: .86em;
  font-family: sans-serif;
  color: rgba(30, 144, 255, 0.5);
  margin-left: 4px;
}
.mission-item > ul > li {
  margin-top: 2px;
}
.mission-item > ul > li::before {
  content: '';
  box-sizing: border-box;
  position: relative;
  left: 0;
  top: 1px;
  display: inline-block;
  width: 12px;
  height: 12px;
  border: solid 2px rgba(30, 144, 255, 0.2);
  border-radius: 2px;
  margin: 0 8px 0 0;
}
@media (min-width: 481px) {
  .mission-list.-single {
    margin-left: 100px;
    margin-right: 100px;
  }
  .mission-list.-single > .mission-item {
    flex: 1 0 auto;
  }
}
.mission-item.-goals > ul > li + li {
  border-top: solid 1px rgba(30, 144, 255, 0.2);
  padding-top: 4px;
  margin: 4px 0;
}
.mission-item.-goals > ul > li::before {
  background: rgba(30, 144, 255, 0.2);
  border-color: transparent;
}
